# Set the form result
$formResult = $form.ShowDialog()

# If the OK button is selected do the following
if ($formResult -eq [System.Windows.Forms.DialogResult]::OK)
{
    if ([string]::IsNullOrEmpty($textboxUsername.Text) -or [string]::IsNullOrEmpty($textboxPassword.Text) -or [string]::IsNullOrEmpty($textboxLiquitZoneURL.Text) -or [string]::IsNullOrEmpty($textboxRecipients.Text) -or [string]::IsNullOrEmpty($textboxWorkspace.Text) -or [string]::IsNullOrEmpty($textboxIdentitySource.Text) -or [string]::IsNullOrEmpty($textboxClientID.Text) -or [string]::IsNullOrEmpty($textboxTenantID.Text) -or [string]::IsNullOrEmpty($textboxClientSecret.Text) -or [string]::IsNullOrEmpty($textboxEntraUserGroup.Text) -or [string]::IsNullOrEmpty($textboxEntraAdminGroup.Text)) {
        [System.Windows.Forms.MessageBox]::Show("All fields must be filled out.")
        Exit
    } else {
        $usr = $textboxUsername.Text
        $password = ConvertTo-SecureString -String $textboxPassword.Text -AsPlainText -Force
        $url = $textboxLiquitZoneURL.Text
        $recipients = $textboxRecipients.Text
        $workspacename = $textboxWorkspace.Text
        $entraname = $textboxIdentitySource.Text
        $clientid = $textboxClientID.Text
        $tenantid = $textboxTenantID.Text
        $clientsecret = $textboxClientSecret.Text
        $entrausergroup = $textboxEntraUserGroup.Text
        $entraadmingroup = $textboxEntraAdminGroup.Text
    }
}

### If the cancel button is selected do the following
if ($formResult -eq [System.Windows.Forms.DialogResult]::Cancel)
{
   [System.Windows.Forms.MessageBox]::Show("User Canceled") 
    Exit
}

# when done, dispose of the stream and form
$stream.Dispose()
$Form.Dispose()

#Requires -RunAsAdministrator
$PackageProvider = "NuGet"
$PackageProviderInstalled = Get-PackageProvider -ListAvailable | Where-Object { $_.Name -eq $PackageProvider }

if ($PackageProviderInstalled) {
    Write-Host "The '$PackageProvider' provider is already installed." -NoNewLine -ForegroundColor green
} else {
    Write-Host "The '$PackageProvider' provider is not installed. Installing..." -NoNewLine -ForegroundColor yellow

    # Install the module using NuGet
   
    Install-PackageProvider -Name NuGet -Force

    Write-Host "'$PackageProvider' has been installed." NoNewLine -ForegroundColor green
}
Write-Host

$moduleName = "Liquit.Server.PowerShell"
$moduleInstalled = Get-Module -ListAvailable | Where-Object { $_.Name -eq $moduleName }

if ($moduleInstalled) {
    Write-Host "The '$moduleName' module is already installed." -NoNewLine -ForegroundColor green
} else {
    Write-Host "The '$moduleName' module is not installed. Installing..." -NoNewLine -ForegroundColor yellow

    # Install the module using NuGet
   
    Install-Module -Name $moduleName -Force

    Write-Host "The module '$moduleName' has been installed." NoNewLine -ForegroundColor green
}
Write-Host

Import-Module -Name "Liquit.Server.PowerShell"

$Credentials = New-Object System.Management.Automation.PSCredential $usr,$password

Connect-LiquitWorkspace -URI "$url" -Credential $credentials

# Set variables
$destinationFolder = "C:\Temp\Liquit\"
$container_url = "<Azure Blob container URL>"
$sas_token = "<Azure SAS Token>"

$container = "Automation/"
$AppVPackageFilename = "XCP-ng Center.appv"
$icon = "XCP-ng.ico"
$tagIcon = "O365.png"
$IconPath = "C:\Temp\Liquit\XCP-ng.ico"
$AppVPackagePath = "C:\Temp\Liquit\XCP-ng Center.appv"
$tagIconPath = "C:\Temp\Liquit\O365.png"

# Check if there is an / at the end of the url
if ($url -match '/$') {
    $url = $url.TrimEnd('/')
} 

$logouturi = "https://login.microsoftonline.com/"+$tenantid+"/oauth2/logout?post_logout_redirect_uri="+$url+"api/auth/token/end"
$authorizationUri = "https://login.microsoftonline.com/"+$tenantid+"/oauth2/authorize"
$tokenUri = "https://login.microsoftonline.com/"+$tenantid+"/oauth2/token"

# Set NEW LiquitIdentitySource (AzureAD) and update
New-LiquitIdentitySource -Type azuread -Methods Federated,Login -Name $entraname -DisplayName $entraname -AzurePhotos Enabled -AzureWriteMode Disabled -Delta $true -AuthorizationUri $authorizationUri -ClientId $clientid -ClientSecret $clientsecret -IncludeNonSecurityGroups $false -RedirectUriMethod Request -UseClientIdAsResource $true -LogoutUri $logouturi -TokenUri $tokenUri
Get-LiquitIdentitySource -Name $entraname | Update-LiquitIdentitySource
 
# Hide LOCAL LiquitIdentitySource
Get-LiquitIdentitySource -ID 00000000-0000-0000-0000-000000000000 | Set-LiquitIdentitySource -Hidden $true

# Create folder C:\Temp\Liquit 
If (-not (Test-Path $destinationFolder)) {
    New-Item -Path $destinationFolder -ItemType Directory -Force
}

# Download the files
Set-Variable ProgressPreference SilentlyContinue ; Invoke-WebRequest -Uri ($container_url + $container + $AppVPackageFilename + $sas_token) -OutFile ($destinationFolder + $AppVPackageFilename)
Set-Variable ProgressPreference SilentlyContinue ; Invoke-WebRequest -Uri ($container_url + $container + $icon + $sas_token) -OutFile ($destinationFolder + $icon)
Set-Variable ProgressPreference SilentlyContinue ; Invoke-WebRequest -Uri ($container_url + $container + $tagIcon + $sas_token) -OutFile ($destinationFolder + $tagIcon)


# Create Liquit Setup Store Connectors
New-LiquitConnector -Name 'Auto-LiquitSetupStore' -Type 'liquitsetupstore' -Method 'Ondemand' -PackageType 'Managed' -Prefix 'LSSA-' -Enabled $true -Settings @{types =  'install','portable','shortcut'; shortcuts = 'true'; filters = 'true'; uninstall = 'true'; shortcutKinds = 'Launch','help'; languages = '2','3'; platforms = '2','3','5','6'}
New-LiquitConnector -Name 'Manual-LiquitSetupStore' -Type 'liquitsetupstore' -Method 'Ondemand' -PackageType 'Managed' -Prefix 'LSSM-' -Enabled $true -Settings @{types =  'install','portable','shortcut'; shortcuts = 'true'; filters = 'true'; uninstall = 'true'; shortcutKinds = 'Launch','help'; languages = '2','3'; platforms = '2','3','5','6'}
 
# Create Liquit Workspace Connectors
New-LiquitConnector -Name 'Demo Workspace' -Type 'liquitworkspace' -Method 'Ondemand' -PackageType 'Managed' -Prefix 'DEMO-' -Enabled $true -Settings @{url = 'https://demo.liquit.com'; username = '<Account>'; password = '<Password>'}
New-LiquitConnector -Name 'Don Workspace' -Type 'liquitworkspace' -Method 'Ondemand' -PackageType 'Managed' -Prefix 'DON-' -Enabled $true -Settings @{url = 'https://donnyvanderlinde.liquit.com'; username = '<Account>'; password = '<Password>'}
  

# Create Scheduled Tasks
New-LiquitScheduledTask -Name 'Auto Update LSS' -Type syncconnector -Triggers @{type = 'daily'; hour = 2; minute = 0} -Enabled $true -Settings @{connector = 'Auto-LiquitSetupStore'; stage = 'Production'; type = 'ApplyUpdates'}
New-LiquitScheduledTask -Name 'Manual Update LSS (Notify)' -Type syncconnector -Triggers @{type = 'daily'; hour = 4; minute = 0} -Enabled $true -Settings @{connector = 'Manual-LiquitSetupStore'; stage = 'Production'} -Notifications @{email = @{enabled = $true; recipients = @($recipients)}}

# Create Access Policies
New-LiquitAccessPolicy -role -privileges @("catalog.cancel",
        "catalog.view",
        "catalog.question.view",
        "catalog.media.view",
        "catalog.request",
        "catalog.review.add",
        "catalog.review.view",
        "content.adduser",
        "workspace.reset",
        "workspace.changepassword",
        "workspace.item.view",
        "workspace.item.autolaunch",
        "workspace.item.favorite",
        "workspace.item.rename",
        "workspace.item.remove",
        "workspace.personal.add",
        "workspace.personal.modify",
        "workspace.personal.remove",
        "workspace.credential.view",
        "workspace.credential.modify",
        "workspace.credential.remove",
        "workspace.approval.view",
        "workspace.approval.reject",
        "workspace.approval.accept",
        "workspace.approval.remove",
        "workspace.tag.view",
        "zone.access.web") -name "Liquit User"
 
# Set Liquit User Permissions 
$accesPolicyUser = Get-LiquitAccessPolicy -Name "Liquit User"
$identityUserGroupID = Get-LiquitIdentity -Type Group | Where-Object DisplayName -EQ $entrausergroup | Select-Object -ExpandProperty ID
$identityUser = Get-LiquitIdentity -ID $identityUserGroupID
New-LiquitPermission -Identity $identityUser -AccessPolicy $accesPolicyUser

# Set Liquit Admin Permissions 
$accesPolicyAdmin = Get-LiquitAccessPolicy -Name "Administrator"
$identityAdminGroupID = Get-LiquitIdentity -Type Group | Where-Object DisplayName -EQ $entraadmingroup | Select-Object -ExpandProperty ID
$identityAdmin = Get-LiquitIdentity -ID $identityAdminGroupID
New-LiquitPermission -Identity $identityAdmin -AccessPolicy $accesPolicyAdmin  


# Creating first example Smart Icon : Microsoft Power BI Desktop
$powerBiIconUrl = "https://www.setupcommander.com/ico/pbidesktop.ico"
$powerBiIconPath = "C:\Windows\Temp\pbidesktop.ico"


$webClient = New-Object System.Net.WebClient
$webClient.DownloadFile($powerBiIconUrl, $powerBiIconPath)
$iconContent = New-LiquitContent -Path $powerBiIconPath

$packageName = "Power BI Desktop"
$packageDisplayName = "Power BI Desktop"
$packageDescription = ""

$packageSI1 = New-LiquitPackage -Name $packageName `
                             -Type "Launch" `
                             -DisplayName $packageDisplayName `
                             -Description $packageDescription `
                             -Priority 100 `
                             -Enabled $true `
                             -Offline $True `
                             -Web $true `
                             -Icon $iconContent 

#create the snapshot for this package
$snapshot = New-LiquitPackageSnapshot -Package $packageSI1

#define launch action set
$actionset = New-LiquitActionSet -snapshot $snapshot `
                                 -Type Launch `
                                 -name 'Launch' `
                                 -Enabled $true `
                                 -Frequency Always `
                                 -Process StopAtFirstEffectiveAction


#define the first launch action
$actionset_action1 = New-LiquitAction -ActionSet $actionset `
                                      -Name 'Start PBIDesktop.exe' `
                                      -Type 'processstart' `
                                      -Enabled $true `
                                      -IgnoreErrors $false `
                                      -Settings @{name = '${ProgramFiles}Microsoft Power BI DesktopbinPBIDesktop.exe'; parameters = "";} `
                                      -Context User


#define the filter set for the first action
$filterset1 = New-LiquitFilterSet -Action $actionset_action1

#add a filter to the first action
New-LiquitFilter -FilterSet $filterset1 -type fileexists -Settings @{path = '${ProgramFiles}Microsoft Power BI DesktopbinPBIDesktop.exe';} -Value "true" 


#set the operator for the entity filter to AND
Set-LiquitEntityFilter -Action $actionset_action1 -Operator Or



#define the second launch action
$actionset_action2 = New-LiquitAction -ActionSet $actionset `
                                      -Name 'Start Windows App MicrosoftPowerBIDesktop' `
                                      -Type 'winapplaunch' `
                                      -Enabled $true `
                                      -IgnoreErrors $false `
                                      -Settings @{identity = 'Microsoft.MicrosoftPowerBIDesktop_8wekyb3d8bbwe'; application = "Microsoft.MicrosoftPowerBIDesktop"; parameters = "";} `
                                      -Context User

#define the filter set for the third action
$filterset2 = New-LiquitFilterSet -Action $actionset_action2

#add a filter to the first action
New-LiquitFilter -FilterSet $filterset2 -type winappinstalled -Settings @{identity = "Microsoft.MicrosoftPowerBIDesktop_8wekyb3d8bbwe";} -Value "true"
                                                                       

#define the third launch action
New-LiquitAction -ActionSet $actionset `
                                      -Name 'Open Power BI online' `
                                      -Type 'openurl' `
                                      -Enabled $true `
                                      -IgnoreErrors $false `
                                      -Settings @{ url = 'https://app.powerbi.com/?noSignUpCheck=1'; browser = 0;} `
                                      -Context User


#publish the package
Publish-LiquitPackageSnapshot -Snapshot $snapshot -stage Production 

# Creating second example Smart Icon : XCP-ng Center (App-V)

#Version ID and Package ID of the App-V package
 $AppV5PackageId = "728497f7-c17f-4b3c-bef4-c572abd4dd9a"
 $AppV5DisplayName = "XCP-ng Center"
 $AppV5ExeName = "XenCenterMain.exe"
 
 
 #define and create the package 
 $packageName = "XCP-ng Center (App-V)"
 $packageDisplayName = "XCP-ng Center (App-V)"
