 $appvContent = New-LiquitContent -Path $AppVPackagePath -FileName $AppVPackageFilename
 New-LiquitAttribute -Entity $actionset_install_action1 -Link $appvContent -ID 'content' -Settings: @{filename = $AppVPackageFilename}
 
 #define the second install action
 $actionset_install_action2 = New-LiquitAction -ActionSet $actionset_install `
